using System;
using System.Collections.Generic;
using System.Web;
using System.Web.UI;
using System.Web.UI.WebControls;
using Reimers.Map;
public partial class _Default : System.Web.UI.Page
{
	protected void Page_Load(object sender, EventArgs e)
	{
		GoogleMap map = new GoogleMap("map");
		map.Options.MapTypes.Clear();
		map.Options.MapTypes.Add(new Reimers.Map.CustomMaps.GoogleHybridMap());
		map.Options.MapTypes.Add(new Reimers.Map.CustomMaps.GoogleMarsElevationMap());
		map.MapControls.Add(new Reimers.Map.Controls.GoogleSmallMapTypeControl(
			"smtc", new Reimers.Map.Controls.GoogleControlPosition(
				 GoogleControlAnchor.G_ANCHOR_TOP_LEFT, new GoogleSize(10, 5)))
			);
		map.ClientSideHandlers.OnDragEnd = "alert(this.getCenter().toUrlValue());";
	}
}

