void GoogleMap_MapLoaded(object sender, BoundsEventArgs e)
{
    foreach (DataRow dr in dt.Rows)
    {
        double lat = (double)dr["Lat"];
        double lng = (double)dr["Lng"];
        if (lat < Bounds.MaxLatitude && lat > Bounds.MinLatitude && lng < Bounds.MaxLongitude && lng > Bounds.MinLongitude)
        {
            e.MapCommand = e.Map.AddOverlay(new GoogleMarker((string)dr["ID"], lat, lng), true);
        }
    }
}

void GoogleMap_Click(object sender, CoordinatesEventArgs e)
{
    GoogleMarker marker = new GoogleMarker("ID", e.Position);
    e.MapCommand = e.Map.AddOverlay(marker, true);
}

void GoogleMap_OverlayClick(object sender, OverlayEventArgs e)
{
    if (e.Overlay is GoogleMarker)
    {
        e.MapCommand = ((GoogleMarker)e.Overlay).OpenInfoWindowHTML(e.Map, "You clicked me");
    }
}

void GMap_RightClick(object sender, PixelEventArgs e)
{
    GoogleMarker marker = new GoogleMarker(DateTime.Now.Millisecond.ToString(), e.Position);
    e.MapCommand = e.Map.AddOverlay(marker, true);
}

void GoogleMap_OverlayRemoved(object sender, OverlayInfoEventArgs e)
{
    e.Map.Overlays.Remove(e.Map.Overlays[e.Map, e.OverlayClientsideID]);
}

void GoogleMap_OverlaysCleared(object sender, MapEventArgs e)
{
    e.Map.Overlays.Clear();
}

SideBar.DisplaySelected = delegate(Reimers.Map.GoogleOverlay Overlay)
{
    if (Overlay is Reimers.Map.GoogleMarker){ return true; }
    return false;
};

remarks.code:
string Sidebar_Click(GoogleOverlay Item)
{
    if (Item is GoogleMarker)
    {
        return ((GoogleMarker)Item).OpenInfoWindowHTML(GoogleMap, "Hello World.");
    }
}

string Sidebar_MouseOver(GoogleOverlay Item)
{
    if (Item is GoogleMarker)
    {
        return GoogleMap.SetCenter(((GoogleMarker)Item).Point);
    }
    else if (Item is GooglePolygon || Item is GooglePolyline)
    {
        return GoogleMap.ZoomToBounds(Item.Bounds);
    }
}

example.code:
 protected void Page_Load(object sender, EventArgs e)
 {
     if (!Page.IsPostBack)
     {
        Reimers.Map.Controls.CustomControl cc =
            new Reimers.Map.Controls.CustomControl(
                "myCC",
                new Reimers.Map.Controls.GoogleControlPosition(
                    GoogleControlAnchor.G_ANCHOR_BOTTOM_RIGHT,
                    new GoogleSize(25, 25)
                    )
            );
        WriteCustomControlContent(cc.ControlContent);
        GMap.Controls.Add(cc);
    }
}

private void WriteCustomControlContent(HtmlTextWriter writer)
{
    writer.AddStyleAttribute("font-weight", "bold");
    writer.AddStyleAttribute("text-align", "center");
    writer.AddStyleAttribute("color", "white");
    writer.AddStyleAttribute("background-color", "red");
    writer.RenderBeginTag("div");
    writer.Write("Hello World");
    writer.RenderEndTag();
}
