keyList1.Add(pk2);
PrimaryKeyList keyList2 = new PrimaryKeyList(typeof(Product));
keyList2.Add(pk2);
keyList2.Add(pk3);

// Use the DefaultQueryStrategy
products1 = pm.GetEntities<Product>(keyList1);
// Specify the QueryStrategy
products2 = pm.GetEntities<Product>(keyList2, QueryStrategy.DataSourceOnly);

