PersistenceManager pm = PersistenceManager.DefaultManager;

// Retrieve an order
Order order = pm.GetEntity<Order>(new PrimaryKey(typeof(Order), 10275));
// Retrieve a product
Product product = pm.GetEntity<Product>(new PrimaryKey(typeof(Product), 1));

// Create an OrderDetail 
OrderDetail dtl = pm.CreateEntity<OrderDetail>();
// assign key fields
dtl.Order = order;
dtl.Product = product;

// add the OrderDetail to the PersistenceManager cache
DebugFns.WriteLine("row state before add = " + dtl.RowState);
dtl.AddToManager();
DebugFns.WriteLine("row state after add = " + dtl.RowState);

