example.code:
// This example shows various ways of constructing and using DataGridViewBindingDescriptors

PersistenceManager pm = PersistenceManager.DefaultManager;
DataGridViewBindingManager mgr = new DataGridViewBindingManager(this.DataGridView1, typeof(Employee));
      
DataGridViewBindingDescriptor d1, d2, d3;

// a simple descriptor
d1 = new DataGridViewBindingDescriptor("First Name", typeof(Employee), "FirstName");

// using a dataconverter
d2 = new DataGridViewBindingDescriptor("Hire Date", typeof(Employee), "HireDate", DataConverter.RequiredDate);

// using a ViewDescriptor
ViewDescriptor vd = new ViewDescriptor("Title", typeof(Employee), "Title");
d3 = new DataGridViewBindingDescriptor(vd);

// add the BindingDescriptors to the BindingManager
mgr.Descriptors.Add(d1);
mgr.Descriptors.Add(d2);
mgr.Descriptors.Add(d3);

// add employees to the BindingManager's DataSource now
mgr.DataSource.AddRange(pm.GetEntities<Employee>());

// This example will bind Customer info to a DataGridView

// Create the BindingManager and BindableList
PersistenceManager pm = PersistenceManager.DefaultManager;
DataGridViewBindingManager mgr = new DataGridViewBindingManager(this.DataGridView1, typeof(Customer));
BindableList<Customer> custList = new BindableList<Customer>();

// Set the DataSource
mgr.BindingSource.DataSource = custList;

// Add descriptors for the bound properties
mgr.Descriptors.Add("Name", "ContactName", DataConverter.RequiredString);
mgr.Descriptors.Add("Company", "CompanyName", DataConverter.ReadOnlyString);
mgr.Descriptors.Add("Telephone", "Phone", new RegexConverter(Editability.Required, @"^\([1-9]\d{2}\)\s?\d{3}\-\d{4}$"));

// Add all Customers to the BindableList
custList.AddRange(pm.GetEntities<Customer>());

