// sample showing use of CurrencyManager to synchronize data in 2 grids

PersistenceManager pm = PersistenceManager.DefaultManager;
BindableList<Employee> employeeList = new BindableList<Employee>();
BindableList<Order> orderList = new BindableList<Order>();
DataGridBindingManager mgrEmp = new DataGridBindingManager(this.dataGrid1, employeeList);
DataGridBindingManager mgrOrders = new DataGridBindingManager(this.dataGrid2, orderList);

//set up a handler for CurrentChanged event - will fire when employee changes in first grid
mgrEmp.CurrencyManager.CurrentChanged += new EventHandler(CurrencyManager_CurrentChanged);

// load all employees
employeeList.AddRange(PersistenceManager.DefaultManager.GetEntities<Employee>());

private void CurrencyManager_CurrentChanged(object sender, EventArgs e) {
   // get the current employee
   Employee emp = (Employee)mgrEmp.CurrencyManager.Current;     
   // refill orderList with orders for current employee
   orderList.ReplaceRange(emp.Orders); 
}
