private void SimpleGridBinding() {
  PersistenceManager pm = PersistenceManager.DefaultManager;

  // setup bindings 
  UltraGridBindingManager mgr = new UltraGridBindingManager(mUltraGrid, typeof(Customer));
  mgr.Descriptors.Add("Customer", "CompanyName");
  mgr.Descriptors.Add("Contact", "ContactName");
  mgr.Descriptors.Add("Phone", "Phone");
  mgr.Descriptors.Add("City", "City");

  // get some data
  mgr.BindingSource.DataSource = pm.GetEntities<Customer>();
}

private void MasterDetailGridBinding() {

  // Sample 3-level display of customers, orders and order details

  BindingSource bindingSource = new BindingSource();
  bindingSource.DataSource = typeof(Customer);

  UltraGridBindingManager mgrMaster = new UltraGridBindingManager(mUltraGrid, bindingSource);
  mgrMaster.Descriptors.Add("Customer", "CompanyName");
  mgrMaster.Descriptors.Add("Contact", "ContactName");
  mgrMaster.Descriptors.Add("Phone", "Phone");
  mgrMaster.Descriptors.Add("City", "City");
  mgrMaster.AssociatedBand.Header.Caption = "Customers";

  UltraGridBindingManager mgrChild = new UltraGridBindingManager(typeof(Order));
  mgrChild.Descriptors.Add("#", "Id");
  mgrChild.Descriptors.Add("Order Date", "OrderDate");
  mgrChild.Descriptors.Add("Freight", "Freight");
  mgrMaster.AddRelationBand(mgrChild, "Orders", "Orders");
  mgrChild.AssociatedBand.HeaderVisible = true;
      
  UltraGridBindingManager mgrDetail = new UltraGridBindingManager(typeof(OrderDetail));
  mgrDetail.Descriptors.Add("Product", "Product.ProductName");
  mgrDetail.Descriptors.Add("Qty", "Quantity");
  mgrDetail.Descriptors.Add("Price", "UnitPrice");
  mgrChild.AddRelationBand(mgrDetail, "Details", "OrderDetails");
  mgrDetail.AssociatedBand.HeaderVisible = true;

  // Get some data
  bindingSource.DataSource = PersistenceManager.DefaultManager.GetEntities<Customer>();
}
